import React, { Component } from 'react';
import '../Game/Game.css';

class CardView extends Component {
  constructor(props) {
    super(props);
    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    if (!this.props.matched && !this.props.imageUp) {
      this.props.onClick(this.props.id,this.props.image);      
    }
  }

  render() {
    let imPath = require('../../../images/');
    let face = '';
    console.log(!this.props.imageUp);
    if (this.props.imageUp) {
      imPath = imPath + this.props.image + '.jpg';
      console.warn(imPath);
      face = 'Front'
    } else {
      imPath = imPath + 'back.jpg';
      console.warn(imPath);
      face = 'Back'
    }

    let className='Card';
    if (this.props.matched) {
      className = className + ' Matched';
    }

    return (
        <img className={className} src={imPath} alt={face} onClick={this.onClick}/>
    );      
  };
};

export default CardView;
